  testEnvironment: "jest-environment-jsdom",
};

// Mock document.getElementById before requiring any modules
global.document = {
  getElementById: jest.fn().mockImplementation(id => {
    if (id === "timeSignature") return { value: "4/4" };
    if (id === "numMeasures") return { value: "4" };
    if (id === "chosenNoteTreble") return { value: "c/4" };
    if (id === "rangeAboveTreble") return { value: "2" };
    if (id === "rangeBelowTreble") return { value: "1" };
    return null;
  }),
  body: { innerHTML: '' }
};

// Mock the entire main.js module
